import numpy as np
import random

def ataque_abejas(param1, param2):
    tablero = np.zeros([param1, param1])

    for bomb in range(param2):
        x = random.randint(0, param1)
        y = random.randint(0, param1)

        tablero[x,y] += 1
        if x+1 &lt; param1:
            tablero[x+1,y] += 1
        if y+1 &lt; param1:
            tablero[x,y+1] += 1
        if 0 &lt; x-1:
            tablero[x-1,y] += 1
        if 0 &lt; y-1:
            tablero[x,y-1] += 1


    return tablero

